mapper.update:
update chapter
set courseid = #{courseid,jdbcType=INTEGER},
  chapter_name = #{chapterName,jdbcType=VARCHAR},
  path = #{path,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
