mapper.update:
update comment
set user_id = #{userId,jdbcType=INTEGER},
  course_id = #{courseId,jdbcType=INTEGER},
  content = #{content,jdbcType=VARCHAR},
  time = #{time,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, user_id, course_id, content, time
from comment
where id = #{id,jdbcType=INTEGER}
